sbox = list(bytes.fromhex(''))

def decrypt(ct, comname=b'r4sti@DESKTOP-J6GEJCI'):
    # pt[i] = pc_name[i % len(pc_name)] ^ (AES_SBOX[ct[i]] - (1 + i));
    pt = b''
    for i in range(len(ct)):
        pt += bytes([comname[i % len(comname)] ^ ((sbox[ct[i]] - (i + 1)) & 0xff)])
    return pt

def encrypt(pt, comname=b'r4sti@DESKTOP-J6GEJCI'):
    ct = b''
    for i in range(len(pt)):
        ct += bytes([sbox.index(((pt[i] ^ comname[i % len(comname)]) + i + 1) & 0xff)])
    return ct

if __name__ == '__main__':
    ct = bytes.fromhex('')
    assert encrypt(decrypt(ct)) == ct